// Generate contextual tip based on pain point
const painPoint = $input.item.json.body.mainPainPoint;
let tip = "";

switch(painPoint) {
  case "Too much time on invoicing/billing":
    tip = "Most businesses can cut invoicing time by 75% just by setting up automatic payment reminders. Even if you do nothing else, this one change is huge.";
    break;
  case "Data entry between systems":
    tip = "Quick win: Check if your current software has CSV import/export features you're not using. You'd be surprised how many people manually enter data that could be uploaded in seconds.";
    break;
  case "Customer communication/follow-ups":
    tip = "Start simple: Create 3-5 email templates for your most common customer questions. This alone can save hours per week.";
    break;
  case "Scheduling and appointment management":
    tip = "Free tool alert: Even a basic scheduling link (like Calendly) can eliminate 90% of back-and-forth scheduling emails.";
    break;
  case "Other workflow challenges":
    tip = "The best automation is often the simplest. Start by timing how long your most repetitive task takes - that's usually where the biggest wins are hiding.";
    break;
  default:
    tip = "Every hour you spend on repetitive tasks is an hour you're not growing your business. Let's fix that.";
}

return {
  tip: tip
};
